mapper.select:
select 

from prescription_template_detail
where pres_temp_name = #{presTempName,jdbcType=VARCHAR}
  and drug_id = #{drugId,jdbcType=INTEGER}

mapper.delete:
delete from prescription_template_detail
where pres_temp_name = #{presTempName,jdbcType=VARCHAR}
  and drug_id = #{drugId,jdbcType=INTEGER}

mapper.update:
update prescription_template_detail
set amount = #{amount,jdbcType=INTEGER},
  dosage = #{dosage,jdbcType=VARCHAR},
  frequency = #{frequency,jdbcType=VARCHAR},
  number_per = #{numberPer,jdbcType=VARCHAR},
  drug_usage = #{drugUsage,jdbcType=VARCHAR},
  deleted = #{deleted,jdbcType=BIT}
where pres_temp_name = #{presTempName,jdbcType=VARCHAR}
  and drug_id = #{drugId,jdbcType=INTEGER}
